mapper.update:
update 	database.users
set  	refresh_token = #{refreshToken}, hash_session = #{hashSession}
where	id = #{id};

mapper.select:
select  id, "name", username, "password", refresh_token, hash_session
from    database.users
where   username = #{username}

select  id, "name", username, "password", email, refresh_token, hash_session
from    database.users
where   refresh_token = #{refreshToken}
        and hash_session = #{hashSession}
